sys_script_include.script:
var checkDupCar = Class.create();
checkDupCar.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	chkDupCar: function(){
	var start = new GlideDateTime(this.getParameter('sysparm_start_date'));
	var end = new GlideDateTime(this.getParameter('sysparm_end_date'));
	var v_num = this.getParameter('sysparm_vehicle_number');

	var gr = new GlideRecord('x_1067756_carparki_car_reserve');
    gr.addQuery('vehicle_number', v_num);
	gr.query();
	while (gr.next()) { 
		if (((gr.start_date <= start) && (gr.end_date >= start)) || ((gr.start_date <= end) && (gr.end_date >= end)))
		{
           gs.info("Number = "+gr.number+" V_num"+gr.vehicle_number);
           return false;	 
		}
	}
	gr.info("not found reccord");
    return true;
	},
	isPublic: true,
    type: 'checkDupCar'
});
